import com.liferay.asset.kernel.model.AssetRendererFactory;
import com.liferay.portal.kernel.util.GetterUtil;
import com.everis.messages.service.builder.service.MessagesLocalServiceUtil;
import com.liferay.portal.kernel.service.UserLocalServiceUtil;

Log logger = LogFactoryUtil.getLog("Log........");

String className = (String)workflowContext.get(WorkflowConstants.CONTEXT_ENTRY_CLASS_NAME);

logger.info("0. Classname" + className);


WorkflowHandler workflowHandler = WorkflowHandlerRegistryUtil.getWorkflowHandler(className);

AssetRendererFactory assetRendererFactory = workflowHandler.getAssetRendererFactory();

logger.info("1. Starting script to send notification 26");

Long recordVersionId = GetterUtil.getLong((String)workflowContext.get(WorkflowConstants.CONTEXT_ENTRY_CLASS_PK));

logger.info("2. recordVersionId - " + recordVersionId);

String groupIdString = (String)workflowContext.get(WorkflowConstants.CONTEXT_GROUP_ID);

logger.info("3. groupIdString - " + groupIdString);

groupId = Long.parseLong(groupIdString);

logger.info("4. groupId - " + groupId);

String userIdString = (String)workflowContext.get(WorkflowConstants.CONTEXT_USER_ID);

userId = Long.parseLong(userIdString);


MessagesLocalServiceUtil.sendNotification26(userId, recordVersionId, groupId);

